// Auto-populate fields after the form is loaded
frappe.web_form.after_load = () => {
    let fields = frappe.web_form.fields;

    // Log all field labels and types
    console.log("Form Fields Information:");
    fields.forEach(function(field) {
        console.log('Field Name: ' + field.fieldname + ', Field Label: ' + field.label + ', Field Type: ' + field.fieldtype);
    });

    console.log("Form has loaded. Fetching user details...");

    // Fetch details of the logged-in user
    frappe.call({
        method: 'sil.services.user_api.get_user_details',
        callback: function(response) {
            console.log("User Details API Response:", response);

            if (response.message) {
                // Auto-populate fields
                console.log("Populating user zone, region, and cluster based on response...");
                frappe.web_form.set_value('custom_zonal_manager', response.message.zone);
                frappe.web_form.set_value('custom_regional_manager', response.message.region_name);
                frappe.web_form.set_value('custom_cluster_manager', response.message.cluster_name);
                frappe.web_form.set_value('custom_attended_by', response.message.emp_name);
                frappe.web_form.set_value('custom_machine_received_by', response.message.emp_name);
            } else {
                console.log("No user details found in response.");
            }
        },
        error: function(error) {
            console.error("Error fetching user details:", error);
        }
    });

    console.log("Attaching event listeners for address fields.");
};

// Handle serial_no field and auto-fill customer details
frappe.web_form.on('custom_item_serial_no', (field, value) => {
    console.log("Serial Number Field Value:", value);

    if (value) {
        frappe.call({
            method: 'sil.services.customer_api.getCustomerBySerialNo',
            args: { serial_no: value },
            callback: function(response) {
                console.log("Customer Details API Response:", response);

                if (response.message) {
                    console.log("Customer found. Populating fields and addresses...");
                    let address_list = response.message.address;
                    let issue_history_list = response.message.issue_history;
                    let item = response.message.item;
                    let item_name = response.message.item_name;
                    let item_family = response.message.item_family;
                    let warranty_start = response.message.warranty_start;
                    let warranty_expiry = response.message.warranty_expiry;
                    let cust_name = response.message.cust_name;

                    console.log("Setting item details:", { item, item_name, item_family });
                    frappe.web_form.set_value('custom_item', item);
                    frappe.web_form.set_value('custom_item_name', item_name);
                    frappe.web_form.set_value('custom_item_group', item_family);

                    if (address_list && address_list.length > 0) {
                        let billing_address = address_list[0].name;
                        let shipping_address = address_list[0].name;

                        // Set customer name, billing, and shipping addresses
                        frappe.web_form.set_value('customer', response.message.cust_name);
                        frappe.web_form.set_value('custom_address', billing_address);
                        // frappe.web_form.set_value('shipping_address', shipping_address);

                        console.log("Setting billing and shipping address options...");
                        let options = address_list.map(address => ({
                            label: `${address.name}, ${address.address_line1}, ${address.city}, ${address.state}, ${address.pincode}`,
                            value: address.name
                        }));

                        frappe.web_form.set_df_property('custom_address', 'options', options);
                        // frappe.web_form.set_df_property('shipping_address', 'options', options);
                    } else {
                        console.log("No address found for the customer. Clearing fields.");
                        frappe.web_form.set_value('custom_address', '');
                        // frappe.web_form.set_value('shipping_address', '');
                    }


                    if (issue_history_list && issue_history_list.length > 0) {
                        console.log("Populating service history...");
                        let service_history_data = [];

                        issue_history_list.forEach(function(item) {
                            service_history_data.push({
                                issue: item.name,          // Ensure these match your child DocType fields
                                date: item.opening_date,   // Ensure these match your child DocType fields
                                complaint: item.subject     // Ensure these match your child DocType fields
                            });
                        });

                       let doc = frappe.web_form.doc;

                        // If the field is not initialized, initialize it as an empty array
                        if (!doc.custom_service_historys) {
                            doc.custom_service_historys = [];
                        }

                        // Clear existing data (optional if you need to reset it)
                        doc.custom_service_historys = [];

                        // Populate the child table with the new data
                        service_history_data.forEach(function(row_data) {
                            doc.custom_service_historys.push({
                                "issue": row_data.issue,
                                "complaint": row_data.complaint,
                                "date": row_data.date
                            });
                        });

                       frappe.web_form.set_values(doc).then(() => {
                        console.log("Child table populated and bound:", doc.custom_service_historys);

                        // Force a refresh of the entire form, including the child table
                        frappe.web_form.refresh();
                        }).catch(err => {
                        console.error("Error while setting form values:", err);
                        });
                    } else {
                        console.log("No issue history found for the customer.");
                    }
                } else {
                    console.log("No customer details found for the serial number.");
                }
            },
            error: function(error) {
                console.error("Error fetching customer details:", error);
            }
        });
    }
});

// Handle customer change and fetch addresses
function handleCustomerChange() {
    let customer_name = frappe.web_form.get_value('customer');
    console.log("Customer Changed: ", customer_name);

    if (customer_name) {
        fetchBillingAddress(customer_name);
        fetchShippingAddress(customer_name);
    } else {
        console.log("No customer selected. Clearing billing and shipping address fields.");
        frappe.web_form.set_value('custom_address', '');
        // frappe.web_form.set_value('shipping_address', '');
    }
}

// Handle billing address input change
function handleBillingAddressChange() {
    let billing_address = frappe.web_form.get_value('custom_address');
    let customer_name = frappe.web_form.get_value('customer');
    console.log("Billing Address Changed:", billing_address);

    if (billing_address) {
        frappe.call({
            method: 'sil.services.customer_api.get_addresses_by_partial_name',
            args: { partial_address: billing_address, customer_name: customer_name },
            callback: function(response) {
                let address_list = response.message.address;
                console.log("Billing Address Search Result:", address_list);

                if (address_list && address_list.length > 0) {
                    let options = address_list.map(address => ({
                        label: `${address.name}, ${address.address_line1}, ${address.city}, ${address.state}, ${address.pincode}`,
                        value: address.name
                    }));
                    frappe.web_form.set_df_property('custom_address', 'options', options);
                    frappe.web_form.set_value('custom_address', options[0].value);
                } else {
                    console.log("No billing addresses found for the input.");
                    frappe.web_form.set_value('billing_address', '');
                }
            }
        });
    } else {
        if (customer_name) {
            console.log("Fetching default billing address for customer: ", customer_name);
            fetchBillingAddress(customer_name);
        }
    }
}

// Fetch billing address options based on customer name
function fetchBillingAddress(customer_name) {
    console.log("Fetching billing addresses for customer: ", customer_name);
    frappe.call({
        method: 'sil.services.customer_api.get_addresses_by_partial_name',
        args: { partial_address: '', customer_name: customer_name },
        callback: function(response) {
            let address_list = response.message.address;
            console.log("Billing Address Result:", address_list);

            if (address_list && address_list.length > 0) {
                let options = address_list.map(address => ({
                    label: `${address.name}, ${address.address_line1}, ${address.city}, ${address.state}, ${address.pincode}`,
                    value: address.name
                }));
                frappe.web_form.set_df_property('custom_address', 'options', options);
                frappe.web_form.set_value('custom_address', options[0].value);
            } else {
                console.log("No billing addresses found for the customer.");
                frappe.web_form.set_value('custom_address', '');
            }
        }
    });
}

frappe.ready(function() {
    // Adding click event listener to the billing_address field
    document.querySelector('[data-fieldname="custom_address"]').addEventListener('click', function() {
        // Get the customer from the web form
        let customer = frappe.web_form.doc.customer;

        // Ensure customer is selected
        if (customer) {
            // Set route options to filter the Address list by the linked customer
            frappe.route_options = {
                'link_name': customer,
                'link_doctype': 'Customer'
            };

            // Set route and capture selection event
            frappe.set_route('List', 'Address').then(() => {
                // When the user clicks on an address, capture the selection
                frappe.listview_settings['Address'] = {
                    onload: function(listview) {
                        listview.page.set_primary_action(__('Select'), function() {
                            // Capture selected rows (addresses)
                            let selected = listview.get_checked_items();

                            if (selected.length > 0) {
                                // Get the first selected address
                                let selected_address = selected[0].name;

                                // Set the billing_address field with the selected address
                                frappe.web_form.set_value('custom_address', selected_address);

                                // Optionally, navigate back to the form
                                frappe.set_route('Form', frappe.web_form.doctype, frappe.web_form.docname);
                            } else {
                                frappe.msgprint(__('Please select an address.'));
                            }
                        });
                    }
                };
            });
        } else {
            // Notify the user if no customer is selected
            frappe.msgprint(__('Please select a customer first.'));
        }
    });
});
