// Auto-populate fields after form is loaded
frappe.web_form.after_load = () => 
{
    console.log("Form has loaded. Fetching user details...");

    // Fetch details of the logged-in user
    frappe.call({
        method: 'sfa_api.services.user_api.get_user_details',
        callback: function(response) {
            console.log("User Details API Response:", response);

            if (response.message) {
                // Auto-populate fields in the form
                console.log("Setting zone:", response.message.zone);
                console.log("Setting region:", response.message.region_name);
                console.log("Setting cluster:", response.message.cluster_name);

                frappe.web_form.set_value('zone', response.message.zone);
                frappe.web_form.set_value('region', response.message.region_name);
                frappe.web_form.set_value('cluster', response.message.cluster_name);
            } else {
                console.log("No user details found in response.");
            }
        },
        error: function(error) {
            console.error("Error fetching user details:", error);
        }
    });

    // Attach event listeners for address fields
    //document.querySelector('[data-fieldname="customer"]').addEventListener('input', handleCustomerChange);
    //document.querySelector('[data-fieldname="billing_address"]').addEventListener('input', handleBillingAddressChange);
    //document.querySelector('[data-fieldname="shipping_address"]').addEventListener('input', handleShippingAddressChange);

    console.log("Event listeners for address fields attached.");
};

// Handle serial_no field and auto-fill customer details
frappe.web_form.on('serial_no', (field, value) => {
    console.log("Serial Number Field Value:", value);

    if (value) {
        console.log("Fetching customer details for serial number:", value);

        frappe.call({
            method: 'sfa_api.services.customer_api.getCustomerBySerialNo',
            args: { serial_no: value },
            callback: function(response) {
                console.log("Customer Details API Response:", response);

                if (response.message) {
                    let address_list = response.message.address;

                    if (address_list && address_list.length > 0) {
                        let billing_address = address_list[0].name;
                        let shipping_address = address_list[0].name;

                        console.log("Customer Name:", response.message.cust_name);
                        console.log("Setting Billing Address:", billing_address);
                        console.log("Setting Shipping Address:", shipping_address);

                        frappe.web_form.set_value('customer', response.message.cust_name);
                        frappe.web_form.set_value('billing_address', billing_address);
                        frappe.web_form.set_value('shipping_address', shipping_address);

                        // Convert address list to options with detailed information
                        let options = address_list.map(address => ({
                            label: `${address.name || ''}, ${address.address_line1 || ''}, ${address.city || ''}, ${address.state || ''}, ${address.pincode || ''}`,
                            value: address.name
                        }));

                        console.log("Address Options:", options);

                        // Set the options for the billing_address field
                        frappe.web_form.set_df_property('billing_address', 'options', options);


                        // Update address options based on the customer
                        //handleCustomerChange();
                    } else {
                        console.log("No address found for customer:", value);
                        // Clear address fields if no addresses found
                        frappe.web_form.set_value('billing_address', '');
                        frappe.web_form.set_value('shipping_address', '');
                    }
                } else {
                    console.log("No customer details found in response.");
                }
            },
            error: function(error) {
                console.error("Error fetching customer details:", error);
            }
        });
    }
});

// Handle customer change and fetch addresses
function handleCustomerChange() {
    let customer_name = frappe.web_form.get_value('customer');
    console.log("Customer selected:", customer_name);

    if (customer_name) {
        fetchBillingAddress(customer_name);
        fetchShippingAddress(customer_name);
    } else {
        console.log("No customer selected.");
        // Clear address fields if no customer is selected
        frappe.web_form.set_value('billing_address', '');
        frappe.web_form.set_value('shipping_address', '');
    }
}

// Handle text change in billing_address field
function handleBillingAddressChange()
{
    let billing_address = frappe.web_form.get_value('billing_address');
    let customer_name = frappe.web_form.get_value('customer');
    console.log("Billing Address Field Value:", billing_address);
    console.log("Customer Name Value:", customer_name);

    if (billing_address) {
        console.log("Fetching data for billing address:", billing_address);

        // Call service to fetch data based on billing_address
        frappe.call({
            method: 'sfa_api.services.customer_api.get_addresses_by_partial_name',
            args: { partial_address: billing_address, customer_name: customer_name },
            callback: function(response) {
                console.log("Billing Address Data API Response:", response);

                if (response.message) {
                    let address_list = response.message.address;

                    if (address_list && address_list.length > 0) {
                        // Convert address list to options with detailed information
                        let options = address_list.map(address => ({
                            label: `${address.name || ''}, ${address.address_line1 || ''}, ${address.city || ''}, ${address.state || ''}, ${address.pincode || ''}`,
                            value: address.name
                        }));

                        console.log("Address Options:", options);

                        // Set the options for the billing_address field
                        frappe.web_form.set_df_property('billing_address', 'options', options);

                        // Optionally, set the first address as the default selected value
                        if (options.length > 0) {
                            frappe.web_form.set_value('billing_address', options[0].value);
                        } else {
                            // If no options are available, clear the field
                            frappe.web_form.set_value('billing_address', '');
                        }
                    } else {
                        frappe.msgprint(__('No addresses found matching the input.'));
                        console.log("No matching addresses found for billing address:", billing_address);
                        // Clear billing address field
                        frappe.web_form.set_value('billing_address', '');
                    }
                } else {
                    frappe.msgprint(__('Failed to fetch addresses.'));
                    console.log("Failed to fetch billing address data.");
                     frappe.web_form.set_value('billing_address', '');
                }
            },
            error: function(error) {
                console.error("Error fetching billing address data:", error);
            }
        });
    }
    else{
     if (customer_name) {
        fetchBillingAddress(customer_name);

    } else {
        console.log("No customer selected.");
        // Clear address fields if no customer is selected
        frappe.web_form.set_value('billing_address', '');
        frappe.web_form.set_value('shipping_address', '');
    }
    }
}

